mapper.select:
	select * from (
    	select rownum rnum, data.* from(
	        select airplane_no, airplane_name, departrue, arrival, price
	        from airplane
	        where ${searchKey} like '%' || #{searchValue} || '%'
    		order by airplane_no desc
    	) data
	) 

		where rnum >= #{start} and rnum <= #{end}
